import React from "react";
import {useForm} from 'react-hook-form'
import classes from './RegisterForm.module.css';

export default function RegisterForm( props ) {

  let {
    register,
    handleSubmit,
    formState: {errors},
  } = useForm()

  let submitForm = (data) => {
        console.log(data)
    }

    let login_input = register('login', {
      required: "Please, fix all errors!"
    })

    let password_input = register('password', {
      required: "Please, fix all errors!"
    })

  return (
    <form onSubmit={handleSubmit(submitForm)} className={classes.contactsFormContainer}>
      <input {...login_input} placeholder="Login" type="text" className={classes.contactsInput} />


      <input {...password_input} placeholder="Password" type="password" className={classes.contactsInput} />

      <button className={classes.submitButton}>Enter</button>
    </form>
  );
}
