import React from "react";
import {useForm} from 'react-hook-form'
import classes from './RegisterForm.module.css';

export default function RegisterForm( props ) {

  let {
    register,
    handleSubmit,
    formState: {errors}
  } = useForm()

  let submitForm = (data) => {
        console.log(data)
    }

    let formError = (error) => {
      console.log("Please, fix all errors!", error);
    }

  return (
    <form onSubmit={handleSubmit(submitForm, formError)} className={classes.contactsFormContainer}>
      <input {...register('login', {required: "Please, fill the login",
              minLength: {
                value: 3,
                message: 'To short'
              },
              maxLength: {
                value: 12,

              }                     
                                })}
            placeholder="Login"   
            type="text" 
            className={classes.contactsInput} 
            />
      {errors.login && <p className="errorField">{errors.login.message}</p>}

      <input {...register('password', {required: "Please, fill the password"})} 
            placeholder="Password" 
            type="password" 
            className={classes.contactsInput} 
            />
      {errors.password && <p className="errorField">{errors.password.message}</p>}

      <button className={classes.submitButton}>Enter</button>
    </form>
  );
}
